// Parse state file
const stateBuffer = $input.first().binary.data;
const stateText = Buffer.from(stateBuffer, 'base64').toString('utf8');
const stateData = JSON.parse(stateText);

return [
  {
    json: {
      channels: stateData.channels || [],
      sent_videos: stateData.sent_videos || [],
      timestamp: new Date().toISOString()
    }
  }
];

// Parse XML RSS YouTube
const xml = $input.first().json.body || $input.first().json.data;
const sentVideos = $('Parse State').first().json.sent_videos;

const videoMatches = xml.matchAll(/<entry>([\s\S]*?)<\/entry>/g);
const videos = [];
const now = new Date();
const maxAgeDays = 3;

for (const match of videoMatches) {
  const entry = match[1];

  const videoIdMatch = entry.match(/<yt:videoId>([^<]+)<\/yt:videoId>/);
  const titleMatch = entry.match(/<title>([^<]+)<\/title>/);
  const publishedMatch = entry.match(/<published>([^<]+)<\/published>/);
  const authorMatch = entry.match(/<name>([^<]+)<\/name>/);

  if (!videoIdMatch || !titleMatch || !publishedMatch) continue;

  const videoId = videoIdMatch[1];
  const title = titleMatch[1];
  const published = publishedMatch[1];
  const channel = authorMatch ? authorMatch[1] : 'Unknown';

  const pubDate = new Date(published);
  const ageDays = Math.floor((now - pubDate) / (1000 * 60 * 60 * 24));

  if (ageDays > maxAgeDays) continue;
  if (sentVideos.includes(videoId)) continue;

  const isShort = title.toLowerCase().includes('#shorts') || title.toLowerCase().includes('#short');
  if (isShort) {
    sentVideos.push(videoId);
    continue;
  }

  videos.push({
    id: videoId,
    title: title,
    channel: channel,
    published: published,
    age_days: ageDays,
    url: `https://www.youtube.com/watch?v=${videoId}`
  });

  sentVideos.push(videoId);
}

return videos.map(v => ({ json: v }));

const video = $input.first().json;
const ageText = video.age_days > 0 ? `${video.age_days} jour(s)` : "aujourd'hui";

return [{
  json: {
    message: `ðŸŽ¬ **${video.title}**\nðŸ‘¤ ${video.channel} â€¢ ðŸ“… Il y a ${ageText}\n${video.url}`,
    video_id: video.id
  }
}];

// Collect all sent video IDs from successful sends
const allItems = $input.all();
const sentIds = allItems.map(item => item.json.video_id).filter(id => id);
const oldState = $('Parse State').first().json;

const updatedSentVideos = [...new Set([...oldState.sent_videos, ...sentIds])];

const newState = {
  channels: oldState.channels,
  sent_videos: updatedSentVideos
};

return [{
  json: {
    state: newState,
    new_videos_count: sentIds.length
  }
}];
